import { createReducer, on } from "@ngrx/store";
import * as StackAction from "../actions/stack.action";
const initialState = [];
export const StackReducer = createReducer(initialState, on(StackAction.GET_STACK_SUCCESS, (state, action) => action.stack), on(StackAction.UPDATE_STACK_SUCCESS, (state, action) => state = action.stack));
